<?phpclass Mage_Adminhtml_Model_System_Config_Backend_BankOfFirstDepositAccountNumber extends Mage_Core_Model_Config_Data{    public function _beforeSave()    {		$bofdn = $this->getValue();		if(empty($bofdn))			Mage::throwException("Banck Of First Deposit Account Number can't be empty!");		if(strlen($bofdn) <5 || strlen($bofdn) > 17)			Mage::throwException("Banck Of First Deposit Account Number must have at least 5 characteres and a maximum of 17 in lenght!");				if (substr($bofdn, -1) == '-' || substr($bofdn, 0) == '-' )  			Mage::throwException("Banck Of First Deposit Account Number can't starts or ends with a dash!");					if(!is_numeric(str_replace("-","",$bofdn)))			Mage::throwException("Banck Of First Deposit Account Number must contain only digits and/or dashes");    }}