<?phpclass Mage_Adminhtml_Model_System_Config_Backend_ReturningRoutingNumber extends Mage_Core_Model_Config_Data{    public function _beforeSave()    {		$bofdn = $this->getValue();		if(empty($bofdn))			Mage::throwException("Returning Routing Number can't be empty!");		if(strlen($bofdn) != 9 || !is_numeric($bofdn))			Mage::throwException("Returning Routing Number must have 9 digits in lenght");				$d = str_split($bofdn,1);		$Check_Digit =  (7*($d[0]+$d[3]+$d[6])+3*($d[1]+$d[4]+$d[7])+9*($d[2]+$d[5]))%10;				$isvalid = false;				if (strlen($bofdn.$Check_Digit)!=10)			$isvalid = false;		else 		{			$d=str_split($bofdn.$Check_Digit,1);			if ((3*($d[0]+$d[3]+$d[6])+7*($d[1]+$d[4]+$d[7])+($d[2]+$d[5]+$d[8]))%10==0)				$isvalid = true;			else				$isvalid = false;		}		if(!$isvalid)			Mage::throwException("Invalid Returning Routing Number!");		    }}